tables.table_schema:
CREATE TABLE network_outage_incidents (
  incident_id INT PRIMARY KEY,
  incident_date DATE,
  root_cause VARCHAR(255),
  location VARCHAR(100)
);

CREATE TABLE network_outage_incidents (
    incident_id INT PRIMARY KEY,
    incident_date TIMESTAMP,
    incident_description TEXT,
    incident_severity INT
);

CREATE TABLE root_causes (
    root_cause_id INT PRIMARY KEY,
    cause_description TEXT,
    incident_id INT,
    FOREIGN KEY (incident_id) REFERENCES network_outage_incidents(incident_id)
);

CREATE TABLE network_outage_incidents (
    incident_id INT PRIMARY KEY,
    incident_date DATE,
    outage_duration INT,
    incident_description TEXT
);

CREATE TABLE root_causes (
    root_cause_id INT PRIMARY KEY,
    cause_description TEXT
);

CREATE TABLE incident_root_cause_mapping (
    incident_id INT,
    root_cause_id INT,
    FOREIGN KEY (incident_id) REFERENCES network_outage_incidents(incident_id),
    FOREIGN KEY (root_cause_id) REFERENCES root_causes(root_cause_id)
);

CREATE TABLE proactive_measures (
    measure_id INT PRIMARY KEY,
    measure_description TEXT
);

CREATE TABLE RootCause (
    id SERIAL PRIMARY KEY,
    cause_name VARCHAR(100),
    impact_level VARCHAR(50),
    mitigation_strategy TEXT
);

CREATE TABLE NetworkDevice (
    id SERIAL PRIMARY KEY,
    device_name VARCHAR(100),
    device_type VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE IncidentRootCause (
    incident_id INT REFERENCES Incident(id),
    root_cause_id INT REFERENCES RootCause(id),
    PRIMARY KEY (incident_id, root_cause_id)
);

CREATE TABLE IncidentDevice (
    incident_id INT REFERENCES Incident(id),
    device_id INT REFERENCES NetworkDevice(id),
    PRIMARY KEY (incident_id, device_id)
);
