tables.table_schema:
CREATE TABLE network_performance_metrics (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    metric_name VARCHAR(50),
    metric_value FLOAT
);

CREATE TABLE network_devices (
  device_id INT PRIMARY KEY,
  device_name VARCHAR(50),
  location VARCHAR(50),
  status VARCHAR(20)
);

CREATE TABLE network_errors (
  error_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  error_code INT,
  error_description TEXT
);

CREATE TABLE network_capacity (
  capacity_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  max_capacity INT,
  utilization FLOAT
);
