tables.table_schema:
CREATE TABLE realtime_data_processing (
    id INT,
    data VARCHAR(255),
    timestamp TIMESTAMP
);

CREATE TABLE edge_computing (
    edge_id INT,
    edge_location VARCHAR(100),
    latency_ms INT
);

CREATE TABLE real_time_data (
    id UUID PRIMARY KEY,
    data_record JSONB
);

CREATE TABLE edge_devices (
    device_id UUID PRIMARY KEY,
    device_name TEXT,
    location GEOMETRY
);

CREATE TABLE processed_data (
    process_id UUID PRIMARY KEY,
    processing_task TEXT,
    result_data JSONB
);

CREATE TABLE edge_devices (
  device_id UUID PRIMARY KEY,
  device_name STRING,
  location STRING
);

CREATE TABLE data_streams (
  stream_id UUID PRIMARY KEY,
  device_id UUID REFERENCES edge_devices(device_id),
  stream_data JSONB,
  timestamp TIMESTAMP
);

CREATE TABLE computations (
  computation_id UUID PRIMARY KEY,
  stream_id UUID REFERENCES data_streams(stream_id),
  computation_type STRING,
  result JSONB
);

CREATE TABLE edge_servers (
  server_id UUID PRIMARY KEY,
  server_name STRING,
  location STRING
);

CREATE TABLE server_logs (
  log_id UUID PRIMARY KEY,
  server_id UUID REFERENCES edge_servers(server_id),
  log_message STRING,
  timestamp TIMESTAMP
);
