tables.table_schema:
CREATE TABLE network_traffic (
    id SERIAL PRIMARY KEY,
    source_ip VARCHAR(255) NOT NULL,
    destination_ip VARCHAR(255) NOT NULL,
    protocol VARCHAR(50) NOT NULL
);

CREATE TABLE threat_alerts (
    alert_id SERIAL PRIMARY KEY,
    timestamp TIMESTAMP NOT NULL,
    alert_type VARCHAR(50) NOT NULL,
    threat_level INT NOT NULL
);

CREATE TABLE threat_signature (
    id INT PRIMARY KEY,
    signature_text TEXT,
    threat_level INT,
    created_at TIMESTAMP
);

CREATE TABLE security_analyst (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(255),
    department VARCHAR(100)
);

CREATE TABLE threat_classification (
    id INT PRIMARY KEY,
    threat_type VARCHAR(255),
    severity_level VARCHAR(50),
    description TEXT
);

CREATE TABLE security_analysts (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100),
    department VARCHAR(100)
);
