tables.table_schema:
CREATE TABLE device_logs (
    device_id UUID PRIMARY KEY,
    log_timestamp TIMESTAMP,
    log_type VARCHAR,
    log_message TEXT
);

CREATE TABLE security_incidents (
    incident_id UUID PRIMARY KEY,
    incident_timestamp TIMESTAMP,
    incident_description TEXT,
    device_id UUID REFERENCES device_logs(device_id)
);

CREATE TABLE security_breaches (
    breach_id UUID PRIMARY KEY,
    breach_timestamp TIMESTAMP,
    breach_description TEXT,
    device_id UUID REFERENCES device_logs(device_id)
);

CREATE TABLE device_logs ( 
    timestamp TIMESTAMP, 
    device_id INT, 
    log_message TEXT 
);

CREATE TABLE security_incidents ( 
    incident_id INT, 
    device_id INT, 
    timestamp TIMESTAMP, 
    description TEXT 
);

CREATE TABLE professionals ( 
    professional_id INT, 
    name TEXT, 
    position TEXT 
);
