tables.table_schema:
CREATE TABLE telecomm_security_experts_incidents (
    incident_id INT PRIMARY KEY,
    incident_description TEXT,
    resolution TEXT
);

CREATE TABLE incident_response_plans (
    id INT PRIMARY KEY,
    incident_description TEXT,
    response_plan TEXT
);

CREATE TABLE security_incidents (
    incident_id INT PRIMARY KEY,
    incident_date DATE,
    incident_description TEXT,
    resolution TEXT
);

CREATE TABLE IncidentResponsePlan (
  id SERIAL PRIMARY KEY,
  plan_name TEXT NOT NULL,
  responsible_team TEXT NOT NULL,
  created_at TIMESTAMP NOT NULL
);

CREATE TABLE IncidentResponsePlan (
  id INT PRIMARY KEY,
  plan_name VARCHAR(255),
  plan_description TEXT
);

CREATE TABLE SecurityIncidents (
  incident_id INT PRIMARY KEY,
  incident_date DATE,
  incident_type VARCHAR(50),
  incident_description TEXT
);

CREATE TABLE IncidentResolution (
  resolution_id INT PRIMARY KEY,
  incident_id INT,
  resolution_date DATE,
  resolution_description TEXT
);

CREATE TABLE TelecomExperts (
  expert_id INT PRIMARY KEY,
  expert_name VARCHAR(100),
  expertise_area VARCHAR(50)
);

CREATE TABLE Incident_Response_Plans (
    id INT PRIMARY KEY,
    plan_name VARCHAR(255),
    description TEXT,
    datetime_created TIMESTAMP
);

CREATE TABLE Incident_Properties (
    id INT PRIMARY KEY,
    property_name VARCHAR(255),
    property_value TEXT
);

CREATE TABLE Incident_Teams (
    id INT PRIMARY KEY,
    team_name VARCHAR(255),
    team_lead VARCHAR(255),
    team_members TEXT
);
