tables.table_schema:
CREATE TABLE traffic_patterns (
    id INT PRIMARY KEY,
    user_id INT,
    timestamp TIMESTAMP,
    data_usage FLOAT
);

CREATE TABLE traffic_patterns (
    id INT PRIMARY KEY,
    date DATE,
    traffic_volume INT
);

CREATE TABLE resource_allocation (
    id INT PRIMARY KEY,
    resource_type VARCHAR(50),
    allocated_capacity INT,
    usage_data JSON
);

CREATE TABLE traffic_patterns (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP NOT NULL,
  source_ip VARCHAR(255) NOT NULL,
  destination_ip VARCHAR(255) NOT NULL
);

CREATE TABLE network_resource_allocation (
  id INT PRIMARY KEY,
  used_bandwidth FLOAT NOT NULL,
  allocated_bandwidth FLOAT NOT NULL,
  time_period DATETIME NOT NULL
);

CREATE TABLE capacity_planning (
  id INT PRIMARY KEY,
  infrastructure_name VARCHAR(255) NOT NULL,
  capacity INT NOT NULL,
  utilization FLOAT NOT NULL
);

CREATE TABLE IF NOT EXISTS resource_allocation (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    network_device_id INT,
    allocated_bandwidth INT
);

CREATE TABLE IF NOT EXISTS capacity_planning (
    id INT PRIMARY KEY,
    timestamp TIMESTAMP,
    network_device_id INT,
    predicted_traffic INT
);
