tables.table_schema:
CREATE TABLE stock_prices (
    ticker_symbol VARCHAR PRIMARY KEY,
    date DATE,
    open_price DECIMAL,
    close_price DECIMAL,
    high_price DECIMAL,
    low_price DECIMAL
);

CREATE TABLE stock_metadata (
    ticker_symbol VARCHAR PRIMARY KEY,
    company_name VARCHAR,
    sector VARCHAR,
    industry VARCHAR
);

CREATE TABLE stock_dividends (
    ticker_symbol VARCHAR,
    ex_dividend_date DATE,
    dividend_amount DECIMAL,
    PRIMARY KEY (ticker_symbol, ex_dividend_date)
);

CREATE TABLE StockSymbols (
    symbol STRING PRIMARY KEY,
    company_name STRING,
    sector STRING
);

CREATE TABLE StockDividends (
    symbol STRING,
    ex_dividend_date DATE,
    dividend_amount DECIMAL
);

CREATE TABLE StockSectors (
    sector_id UUID PRIMARY KEY,
    sector_name STRING
);

CREATE TABLE stock_prices (
    price_id SERIAL PRIMARY KEY,
    stock_id INT REFERENCES stocks(stock_id),
    price DECIMAL(10, 2) NOT NULL,
    price_date DATE NOT NULL
);

CREATE TABLE stock_analytics (
    analytics_id SERIAL PRIMARY KEY,
    stock_id INT REFERENCES stocks(stock_id),
    trend_analysis TEXT NOT NULL,
    analysis_date DATE NOT NULL
);

CREATE TABLE stock_dividends (
    dividend_id SERIAL PRIMARY KEY,
    stock_id INT REFERENCES stocks(stock_id),
    dividend_amount DECIMAL(10, 2) NOT NULL,
    dividend_date DATE NOT NULL
);
