tables.table_schema:
CREATE TABLE Trader (
  trader_id SERIAL PRIMARY KEY,
  trader_name VARCHAR(50) NOT NULL,
  trader_email VARCHAR(100) UNIQUE NOT NULL,
  trader_phone VARCHAR(20)
);

CREATE TABLE trade_orders (
    order_id UUID PRIMARY KEY,
    order_type VARCHAR(50) NOT NULL,
    order_status VARCHAR(50) NOT NULL,
    order_timestamp TIMESTAMP NOT NULL
);

CREATE TABLE trade_accounts (
    account_id UUID PRIMARY KEY,
    account_name VARCHAR(50) NOT NULL,
    account_balance DECIMAL(15, 2) NOT NULL
);

CREATE TABLE trade_assets (
    asset_id UUID PRIMARY KEY,
    asset_name VARCHAR(100) NOT NULL,
    asset_type VARCHAR(50) NOT NULL,
    asset_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE trade_users (
    user_id UUID PRIMARY KEY,
    user_name VARCHAR(50) NOT NULL,
    user_email VARCHAR(100) NOT NULL,
    user_phone VARCHAR(15) NOT NULL
);
