tables.table_schema:
CREATE TABLE portfolios (
    portfolio_id UUID PRIMARY KEY,
    portfolio_name VARCHAR(255) NOT NULL,
    client_id UUID NOT NULL,
    created_at TIMESTAMP NOT NULL
);

CREATE TABLE performance_reports (
    report_id UUID PRIMARY KEY,
    portfolio_id UUID NOT NULL,
    report_date DATE NOT NULL,
    performance DECIMAL(5,2) NOT NULL
);

CREATE TABLE investment_portfolio (
  portfolio_id UUID PRIMARY KEY,
  portfolio_name VARCHAR(255) NOT NULL,
  portfolio_description TEXT,
  created_date TIMESTAMP
);

CREATE TABLE portfolio_investment (
  id UUID PRIMARY KEY,
  portfolio_id UUID REFERENCES investment_portfolio(portfolio_id),
  investment_id UUID REFERENCES investment(investment_id),
  investment_date TIMESTAMP
);

CREATE TABLE investment_performance (
  performance_id UUID PRIMARY KEY,
  investment_id UUID REFERENCES investment(investment_id),
  performance_date TIMESTAMP,
  performance_result NUMERIC
);
