tables.table_schema:
CREATE TABLE pricing_data (
    id SERIAL PRIMARY KEY,
    stock_symbol VARCHAR(10) NOT NULL,
    price DECIMAL NOT NULL,
    date DATE NOT NULL
);

CREATE TABLE volatility_data (
    id SERIAL PRIMARY KEY,
    stock_symbol VARCHAR(10) NOT NULL,
    volatility DECIMAL NOT NULL,
    date DATE NOT NULL
);

CREATE TABLE volatility_data (
  symbol VARCHAR(255),
  date DATE,
  volatility DECIMAL(10, 4)
);

CREATE TABLE market_risk_analysis (
  symbol VARCHAR(255),
  date DATE,
  risk_score DECIMAL(10, 4),
  risk_category VARCHAR(50)
);

CREATE TABLE historical_pricing (
    security_id INT PRIMARY KEY,
    security_name STRING,
    price DECIMAL,
    trade_date DATE
);

CREATE TABLE volatility_data (
    security_id INT PRIMARY KEY,
    volatility DECIMAL,
    volatility_date DATE
);

CREATE TABLE market_assets (
    asset_id INT PRIMARY KEY,
    asset_name STRING
);

CREATE TABLE risk_analysis (
    analysis_id INT PRIMARY KEY,
    analysis_date DATE,
    risk_score DECIMAL
);
