CREATE TABLE corporate_actions (
    action_id INT PRIMARY KEY,
    action_name VARCHAR(255),
    action_type VARCHAR(50),
    action_date DATE,
    company_id INT,
    FOREIGN KEY (company_id) REFERENCES companies(company_id)
);

CREATE TABLE dividend_payments (
    payment_id INT PRIMARY KEY,
    payment_date DATE,
    payment_amount DECIMAL(10, 2),
    recipient_id INT,
    FOREIGN KEY (recipient_id) REFERENCES shareholders(shareholder_id)
);

CREATE TABLE companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255)
);

CREATE TABLE CorporateActions (
    action_id INT PRIMARY KEY,
    action_description TEXT,
    action_date DATE
);

CREATE TABLE DividendPayments (
    payment_id INT PRIMARY KEY,
    company_id INT,
    payment_amount DECIMAL(10,2),
    payment_date DATE
);

CREATE TABLE Companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    industry VARCHAR(100)
);

CREATE TABLE Shareholders (
    shareholder_id INT PRIMARY KEY,
    company_id INT,
    shareholder_name VARCHAR(255),
    shares_owned INT
);

CREATE TABLE CorporateActions (
    id UUID PRIMARY KEY,
    action_date DATE,
    action_type VARCHAR(50),
    description TEXT
);

CREATE TABLE DividendPayments (
    id UUID PRIMARY KEY,
    payment_date DATE,
    recipient_id UUID,
    amount DECIMAL(10, 2)
);

CREATE TABLE Recipients (
    id UUID PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100),
    address TEXT
);

CREATE TABLE ActionRecipients (
    action_id UUID,
    recipient_id UUID,
    FOREIGN KEY(action_id) REFERENCES CorporateActions(id),
    FOREIGN KEY(recipient_id) REFERENCES Recipients(id)
);
