tables.table_schema:
CREATE TABLE Investment_Portfolio (
    portfolio_id INT PRIMARY KEY,
    portfolio_name VARCHAR(255),
    investment_amount DECIMAL(10, 2),
    investment_type VARCHAR(50)
);

CREATE TABLE Scenario_Analysis (
    scenario_id INT PRIMARY KEY,
    portfolio_id INT,
    stress_test_results VARCHAR(100),
    impact_on_portfolio DECIMAL(5, 2)
);

CREATE TABLE investment_portfolio (
  portfolio_id INT PRIMARY KEY,
  portfolio_name VARCHAR(255) NOT NULL,
  investment_amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE scenario_analysis (
  scenario_id INT PRIMARY KEY,
  scenario_name VARCHAR(255) NOT NULL,
  impact DECIMAL(10, 2) NOT NULL
);

CREATE TABLE portfolio_scenario (
  id INT PRIMARY KEY,
  portfolio_id INT REFERENCES investment_portfolio(portfolio_id),
  scenario_id INT REFERENCES scenario_analysis(scenario_id)
);

CREATE TABLE Portfolio (
    portfolio_id INT PRIMARY KEY,
    portfolio_name VARCHAR(255),
    created_date DATE
);

CREATE TABLE Investment (
    investment_id INT PRIMARY KEY,
    portfolio_id INT,
    investment_name VARCHAR(255),
    amount DECIMAL(10,2)
);

CREATE TABLE QueryResult (
    result_id INT PRIMARY KEY,
    scenario_id INT,
    query_name VARCHAR(255),
    query_result VARCHAR(255)
);
