tables.table_schema:
CREATE TABLE asset_classes (
  class_id SERIAL PRIMARY KEY,
  asset_class_name VARCHAR(50) NOT NULL,
  description TEXT
);

CREATE TABLE InvestmentPerformance (
    id UUID PRIMARY KEY,
    asset_class VARCHAR(50),
    investment_amount DECIMAL,
    investment_date DATE
);

CREATE TABLE AssetClasses (
    id UUID PRIMARY KEY,
    asset_name VARCHAR(50),
    asset_type VARCHAR(50)
);

CREATE TABLE InvestmentReturns (
    id UUID PRIMARY KEY,
    investment_id UUID,
    return_amount DECIMAL,
    return_date DATE
);

CREATE TABLE PerformanceAnalysis (
    id UUID PRIMARY KEY,
    investment_id UUID,
    analysis_type VARCHAR(50),
    analysis_result DECIMAL
);

CREATE TABLE Assets (
    asset_id INT PRIMARY KEY,
    asset_name VARCHAR(50) NOT NULL,
    asset_class VARCHAR(50) NOT NULL
);

CREATE TABLE Investments (
    investment_id INT PRIMARY KEY,
    asset_id INT,
    investment_amount DECIMAL(10,2) NOT NULL,
    investment_date DATE NOT NULL
);

CREATE TABLE Returns (
    return_id INT PRIMARY KEY,
    investment_id INT,
    return_percentage DECIMAL(5,2) NOT NULL,
    return_date DATE NOT NULL
);

CREATE TABLE Asset_Classes (
    class_id INT PRIMARY KEY,
    class_name VARCHAR(50) NOT NULL
);

CREATE TABLE Asset_Class_Mapping (
    asset_id INT,
    class_id INT,
    PRIMARY KEY (asset_id, class_id)
);
