tables.table_schema:
CREATE TABLE customer_credit_scores (
  customer_id UUID,
  credit_score INT,
  credit_history TEXT
);

CREATE TABLE CustomerCreditScores (
    customer_id INT PRIMARY KEY,
    credit_score INT
);

CREATE TABLE CustomerCreditHistory (
    customer_id INT PRIMARY KEY,
    credit_limit DECIMAL,
    repayment_history TEXT
);

CREATE TABLE CustomerCreditScores (
  customer_id INT PRIMARY KEY,
  credit_score INT
);

CREATE TABLE LoanHistories (
  loan_id INT PRIMARY KEY,
  customer_id INT,
  loan_amount DECIMAL,
  loan_date DATE
);

CREATE TABLE CreditCardHistories (
  card_id INT PRIMARY KEY,
  customer_id INT,
  credit_limit DECIMAL,
  card_type VARCHAR(50)
);

CREATE TABLE customers (
    id INT PRIMARY KEY,
    name VARCHAR(50),
    address VARCHAR(100),
    credit_score INT
);

CREATE TABLE loans (
    loan_id INT PRIMARY KEY,
    customer_id INT,
    loan_amount DECIMAL(10, 2),
    loan_status VARCHAR(50)
);

CREATE TABLE payments (
    payment_id INT PRIMARY KEY,
    loan_id INT,
    payment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE credit_cards (
    card_number VARCHAR(16) PRIMARY KEY,
    customer_id INT,
    card_limit DECIMAL(10, 2),
    card_status VARCHAR(50)
);

CREATE TABLE credit_history (
    history_id INT PRIMARY KEY,
    customer_id INT,
    credit_score INT,
    history_date DATE
);
