tables.table_schema:
CREATE TABLE loan_applications (
    application_id INT PRIMARY KEY,
    applicant_name VARCHAR(50),
    loan_amount DECIMAL(10,2),
    application_date DATE
);

CREATE TABLE loan_approval (
    approval_id INT PRIMARY KEY,
    application_id INT REFERENCES loan_applications(application_id),
    approval_date DATE,
    approval_status VARCHAR(20)
);

CREATE TABLE applicant_details (
    applicant_id INT PRIMARY KEY,
    applicant_name VARCHAR(50),
    applicant_address VARCHAR(100),
    applicant_phone VARCHAR(15)
);

CREATE TABLE credit_score (
    credit_id INT PRIMARY KEY,
    applicant_id INT REFERENCES applicant_details(applicant_id),
    score INT,
    last_updated DATE
);
