tables.table_schema:
CREATE TABLE ApprovalCriteria (
  criteria_id UUID PRIMARY KEY,
  criteria_name VARCHAR(255),
  minimum_score INT,
  maximum_loan_amount DECIMAL(10,2)
);

CREATE TABLE loan_applications (
    application_id UUID PRIMARY KEY,
    applicant_name TEXT,
    loan_amount DECIMAL,
    credit_score INT
);

CREATE TABLE criteria_rules (
    rule_id UUID PRIMARY KEY,
    rule_name TEXT,
    min_credit_score INT,
    max_loan_amount DECIMAL
);

CREATE TABLE loan_approvals (
    approval_id UUID PRIMARY KEY,
    application_id UUID,
    rule_id UUID,
    approval_date TIMESTAMP
);

CREATE TABLE credit_scores (
    credit_score_id UUID PRIMARY KEY,
    applicant_name TEXT,
    credit_score INT,
    score_date DATE
);
