tables.table_schema:
CREATE TABLE reminders (
    reminder_id INT PRIMARY KEY,
    reminder_description TEXT,
    due_date TIMESTAMP,
    amount DECIMAL
);

CREATE TABLE Users (
    user_id INT PRIMARY KEY,
    username VARCHAR,
    email VARCHAR,
    phone_number VARCHAR
);

CREATE TABLE ScheduledQueries (
    query_id INT PRIMARY KEY,
    query_text TEXT,
    schedule_datetime DATETIME,
    active BOOLEAN
);

CREATE TABLE RemindersLogs (
    log_id INT PRIMARY KEY,
    reminder_id INT,
    log_date DATETIME,
    status VARCHAR
);

CREATE TABLE bills (bill_id INT PRIMARY KEY,
	bill_name VARCHAR(50) NOT NULL,
	amount DECIMAL(10,2) NOT NULL,
	due_date DATE NOT NULL
);

CREATE TABLE reminders (reminder_id INT PRIMARY KEY,
	bill_id INT REFERENCES bills(bill_id),
	scheduled_time TIMESTAMP NOT NULL,
	sent BOOLEAN NOT NULL DEFAULT FALSE
);

CREATE TABLE users (user_id INT PRIMARY KEY,
	username VARCHAR(50) UNIQUE NOT NULL,
	email VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE scheduled_queries (query_id INT PRIMARY KEY,
	user_id INT REFERENCES users(user_id),
	query_text TEXT NOT NULL,
	schedule_interval INTERVAL NOT NULL
);

CREATE TABLE logs (log_id INT PRIMARY KEY,
	query_id INT REFERENCES scheduled_queries(query_id),
	execution_time TIMESTAMP NOT NULL,
	execution_result TEXT
);
