tables.table_schema:
CREATE TABLE fraud_accounts ( 
    account_id INT PRIMARY KEY, 
    account_name VARCHAR(255) NOT NULL, 
    blacklist_reason TEXT
);

CREATE TABLE fraudulent_accounts (
    account_id INT NOT NULL,
    account_name VARCHAR(255) NOT NULL,
    reason_for_blacklist TEXT NOT NULL
);

CREATE TABLE blacklist_history (
    transaction_id INT NOT NULL,
    account_id INT NOT NULL,
    blacklist_date DATE NOT NULL,
    note TEXT
);

CREATE TABLE fraudulent_patterns (
    pattern_id INT NOT NULL,
    pattern_description TEXT NOT NULL
);

CREATE TABLE fraudulent_accounts (
    account_id INT PRIMARY KEY,
    account_number VARCHAR(20) NOT NULL,
    account_type VARCHAR(10) NOT NULL,
    blacklisted BOOLEAN
);

CREATE TABLE flagged_transactions (
    transaction_id INT PRIMARY KEY,
    account_id INT NOT NULL,
    amount DECIMAL(10, 2) NOT NULL,
    transaction_date DATE NOT NULL,
    reason VARCHAR(100) NOT NULL
);

CREATE TABLE blacklist_logs (
    log_id INT PRIMARY KEY,
    account_id INT NOT NULL,
    action_taken VARCHAR(50) NOT NULL,
    log_date TIMESTAMP NOT NULL
);

CREATE TABLE account_notes (
    note_id INT PRIMARY KEY,
    account_id INT NOT NULL,
    note_text TEXT NOT NULL,
    note_date TIMESTAMP NOT NULL
);

CREATE TABLE fraudulent_accounts (
    id INT PRIMARY KEY,
    account_number INT UNIQUE,
    account_holder VARCHAR(255),
    fraud_reason TEXT
);

CREATE TABLE fraud_patterns (
    id INT PRIMARY KEY,
    pattern_name VARCHAR(255) UNIQUE,
    description TEXT
);

CREATE TABLE fraud_reports (
    id INT PRIMARY KEY,
    report_id INT UNIQUE,
    account_number INT,
    report_date DATE,
    report_details TEXT
);

CREATE TABLE blacklist_reasons (
    id INT PRIMARY KEY,
    reason_code INT UNIQUE,
    reason_description VARCHAR(255)
);

CREATE TABLE blacklist_accounts (
    id INT PRIMARY KEY,
    account_number INT UNIQUE,
    blacklisted_date DATE,
    reason_code INT
);
