tables.table_schema:
CREATE TABLE fraud_activities (
    activity_id UUID PRIMARY KEY,
    activity_type VARCHAR(50),
    activity_date TIMESTAMP,
    activity_description TEXT
);

CREATE TABLE fraudulent_users (
    user_id UUID PRIMARY KEY,
    user_name VARCHAR(50),
    registration_date TIMESTAMP,
    is_suspected_fraudster BOOLEAN
);

CREATE TABLE fraudulent_activities (
    activity_id INT PRIMARY KEY,
    activity_type VARCHAR(50),
    activity_date DATE,
    activity_description TEXT
);

CREATE TABLE fraudulent_participants (
    participant_id INT PRIMARY KEY,
    participant_name VARCHAR(100),
    participant_role VARCHAR(50),
    participant_contact_info TEXT
);

CREATE TABLE fraud_detection (
    detection_id INT PRIMARY KEY,
    detection_type VARCHAR(50),
    detection_date DATE,
    detection_details TEXT
);

CREATE TABLE fraudulent_transactions (
    transaction_id INT PRIMARY KEY,
    transaction_date DATE,
    transaction_amount DECIMAL(10, 2),
    transaction_description TEXT
);

CREATE TABLE fraud_reports (
    report_id INT PRIMARY KEY,
    report_date DATE,
    report_author VARCHAR(100),
    report_details TEXT
);
