tables.table_schema:
CREATE TABLE blacklisted_ips
(
    ip_address VARCHAR(15) PRIMARY KEY,
    reason TEXT
);

CREATE TABLE fraud_attempts
(
    id SERIAL PRIMARY KEY,
    ip_address VARCHAR(15) REFERENCES blacklisted_ips(ip_address),
    timestamp TIMESTAMP
);

CREATE TABLE blacklisted_ip_addresses (
   ip_address VARCHAR(15) PRIMARY KEY,
   reason TEXT,
   date_added TIMESTAMP
);

CREATE TABLE blacklisted_ips (
    ip_address VARCHAR(15) PRIMARY KEY,
    reason TEXT,
    blacklisted_at TIMESTAMPTZ
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    email VARCHAR(100) NOT NULL,
    created_at TIMESTAMPTZ
);

CREATE TABLE transactions (
    transaction_id UUID PRIMARY KEY,
    user_id UUID REFERENCES users(user_id),
    amount NUMERIC(10, 2) NOT NULL,
    transaction_date TIMESTAMPTZ
);

CREATE TABLE fraud_attempts (
    attempt_id UUID PRIMARY KEY,
    ip_address VARCHAR(15) REFERENCES blacklisted_ips(ip_address),
    user_id UUID REFERENCES users(user_id),
    attempt_date TIMESTAMPTZ
);

CREATE TABLE audit_logs (
    log_id UUID PRIMARY KEY,
    action VARCHAR(50) NOT NULL,
    user_id UUID REFERENCES users(user_id),
    log_date TIMESTAMPTZ
);
