tables.table_schema:
CREATE TABLE rewards_points (
  transaction_id INT PRIMARY KEY,
  cardholder_name VARCHAR(50),
  purchase_amount DECIMAL(10, 2),
  points_earned INT
);

CREATE TABLE rewards_points (
    reward_id INT PRIMARY KEY,
    customer_id INT NOT NULL,
    purchase_amount DECIMAL NOT NULL,
    earn_date DATE NOT NULL
);

CREATE TABLE reward_history (
    history_id INT PRIMARY KEY,
    customer_id INT NOT NULL,
    reward_id INT NOT NULL,
    transaction_date DATE NOT NULL
);

CREATE TABLE rewards_points (
    customer_id INTEGER,
    transaction_date DATE,
    purchase_amount DECIMAL,
    points_earned INTEGER
);

CREATE TABLE customers (
    customer_id INTEGER PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE credit_cards (
    card_number VARCHAR(16) PRIMARY KEY,
    expiration_date DATE,
    customer_id INTEGER,
    card_type VARCHAR(20)
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE
);

CREATE TABLE transactions (
    transaction_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    amount DECIMAL(10, 2) NOT NULL,
    transaction_date TIMESTAMP
);

CREATE TABLE cards (
    card_id INT PRIMARY KEY,
    user_id INT NOT NULL,
    card_number VARCHAR(16) NOT NULL,
    expiration_date DATE
);

CREATE TABLE rewards_members (
  member_id INT PRIMARY KEY,
  member_name VARCHAR,
  rewards_points INT
);

CREATE TABLE credit_cards (
  card_number INT PRIMARY KEY,
  card_holder VARCHAR,
  credit_limit INT
);

CREATE TABLE purchases (
  purchase_id INT PRIMARY KEY,
  card_number INT,
  purchase_amount INT,
  purchase_date TIMESTAMP
);

CREATE TABLE rewards_catalog (
  item_id INT PRIMARY KEY,
  item_name VARCHAR,
  required_points INT
);

CREATE TABLE redeemed_rewards (
  redemption_id INT PRIMARY KEY,
  member_id INT,
  item_id INT,
  redemption_date TIMESTAMP
);
