tables.table_schema:
CREATE TABLE CreditCardTransactions (
    transaction_id INT PRIMARY KEY,
    credit_card_number VARCHAR(16),
    cvv_code VARCHAR(3),
    transaction_amount DECIMAL(10,2)
);

CREATE TABLE AuthorizedTransactions (
    authorization_id INT PRIMARY KEY,
    transaction_id INT,
    authorization_code VARCHAR(10),
    authorization_date TIMESTAMP
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    full_name VARCHAR(50) NOT NULL,
    email VARCHAR(50) NOT NULL,
    phone_number VARCHAR(15) NOT NULL
);

CREATE TABLE CreditCards (
  card_number INT,
  expiry_date DATE,
  cvv_code INT
);

CREATE TABLE Transactions (
  transaction_id INT,
  card_number INT,
  transaction_date TIMESTAMP,
  amount DECIMAL
);

CREATE TABLE Customers (
  customer_id INT,
  name VARCHAR,
  address VARCHAR
);

CREATE TABLE FraudTransactions (
  transaction_id INT,
  reason VARCHAR,
  is_flagged BOOLEAN
);

CREATE TABLE users (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  password VARCHAR(50) NOT NULL
);

CREATE TABLE cards (
  card_id SERIAL PRIMARY KEY,
  card_number VARCHAR(16) NOT NULL,
  expiration_date DATE NOT NULL,
  cvv_code VARCHAR(3) NOT NULL
);

CREATE TABLE transactions_history (
  transaction_id SERIAL PRIMARY KEY,
  card_id INT NOT NULL,
  transaction_amount DECIMAL NOT NULL,
  transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE cvv_logs (
  log_id SERIAL PRIMARY KEY,
  card_id INT NOT NULL,
  cvv_attempt VARCHAR(3) NOT NULL,
  log_date TIMESTAMP NOT NULL
);
