CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(255) NOT NULL,
    customer_email VARCHAR(255) UNIQUE
);

CREATE TABLE merchants (
    merchant_id INT PRIMARY KEY,
    merchant_name VARCHAR(255) NOT NULL,
    merchant_location VARCHAR(255)
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL,
  address TEXT
);

CREATE TABLE Merchants (
  merchant_id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  category VARCHAR(50),
  address TEXT
);

CREATE TABLE customer (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50) NOT NULL,
  card_number VARCHAR(16) NOT NULL
);

CREATE TABLE merchant (
  merchant_id INT PRIMARY KEY,
  merchant_name VARCHAR(50) NOT NULL,
  address VARCHAR(100) NOT NULL
);

CREATE TABLE dispute (
  dispute_id INT PRIMARY KEY,
  customer_id INT REFERENCES customer(customer_id),
  merchant_id INT REFERENCES merchant(merchant_id),
  amount DECIMAL(10,2) NOT NULL
);

CREATE TABLE dispute_resolution (
  resolution_id INT PRIMARY KEY,
  dispute_id INT REFERENCES dispute(dispute_id),
  resolution_date DATE NOT NULL,
  resolution_status VARCHAR(20) NOT NULL
);

CREATE TABLE customers (
  customer_id UUID PRIMARY KEY,
  customer_name VARCHAR(255),
  customer_email VARCHAR(255),
  customer_phone VARCHAR(20)
);

CREATE TABLE merchants (
  merchant_id UUID PRIMARY KEY,
  merchant_name VARCHAR(255),
  merchant_email VARCHAR(255),
  merchant_phone VARCHAR(20)
);

CREATE TABLE resolution (
  resolution_id UUID PRIMARY KEY,
  dispute_id UUID,
  resolution_date DATE,
  resolution_description TEXT
);
