tables.table_schema:
CREATE TABLE loans (
 loan_id INT PRIMARY KEY,
 borrower_name VARCHAR(50),
 loan_amount DECIMAL,
 encrypted_ssn VARCHAR(100)
);

CREATE TABLE loan_officers (
 officer_id INT PRIMARY KEY,
 officer_name VARCHAR(50),
 department VARCHAR(50),
 access_level INT
);

CREATE TABLE users (
    user_id INT PRIMARY KEY,
    username TEXT,
    password_hash VARCHAR,
    role TEXT
);

CREATE TABLE access_control (
    access_id INT PRIMARY KEY,
    user_id INT,
    table_name TEXT,
    access_level TEXT
);

CREATE TABLE LoanApplications (
    application_id UUID PRIMARY KEY,
    applicant_name VARCHAR(255),
    loan_amount DECIMAL,
    status VARCHAR(50)
);

CREATE TABLE LoanOfficers (
    officer_id UUID PRIMARY KEY,
    officer_name VARCHAR(255),
    department VARCHAR(100),
    email VARCHAR(255)
);

CREATE TABLE LoanApprovals (
    approval_id UUID PRIMARY KEY,
    application_id UUID REFERENCES LoanApplications(application_id),
    approval_date TIMESTAMP,
    approval_status VARCHAR(50)
);

CREATE TABLE LoanSecurity (
    security_id UUID PRIMARY KEY,
    security_level VARCHAR(50),
    encryption_key VARCHAR(255),
    access_control_list VARCHAR(255)
);

CREATE TABLE LoanApplications (
  application_id INT PRIMARY KEY,
  applicant_name VARCHAR(50),
  loan_amount DECIMAL,
  loan_status VARCHAR(20)
);

CREATE TABLE Customers (
  customer_id INT PRIMARY KEY,
  customer_name VARCHAR(50),
  email_address VARCHAR(100),
  phone_number VARCHAR(15)
);

CREATE TABLE LoanOfficers (
  officer_id INT PRIMARY KEY,
  officer_name VARCHAR(50),
  department VARCHAR(30),
  access_level VARCHAR(10)
);

CREATE TABLE LoanDecisions (
  decision_id INT PRIMARY KEY,
  application_id INT,
  decision_date DATE,
  decision_result VARCHAR(10)
);

CREATE TABLE SecurityLogs (
  log_id INT PRIMARY KEY,
  log_date TIMESTAMP,
  event_description TEXT,
  user_id INT
);
