tables.table_schema:
CREATE TABLE financial_calculators (
  calculator_id UUID PRIMARY KEY,
  calculator_name VARCHAR(100) NOT NULL,
  calculation_formula TEXT NOT NULL,
  last_updated TIMESTAMP
);

CREATE TABLE real_time_data (
  data_id UUID PRIMARY KEY,
  data_source VARCHAR(100) NOT NULL,
  data_value DECIMAL NOT NULL,
  data_timestamp TIMESTAMP
);

CREATE TABLE financial_calculator_assets (
    asset_id UUID PRIMARY KEY,
    asset_name VARCHAR(100),
    asset_value DECIMAL
);
CREATE TABLE financial_calculator_liabilities (
    liability_id UUID PRIMARY KEY,
    liability_name VARCHAR(100),
    liability_value DECIMAL
);
CREATE TABLE financial_calculator_income (
    income_id UUID PRIMARY KEY,
    income_source VARCHAR(100),
    income_amount DECIMAL
);
CREATE TABLE financial_calculator_expenses (
    expense_id UUID PRIMARY KEY,
    expense_category VARCHAR(100),
    expense_amount DECIMAL
);

CREATE TABLE CalculatorUser (
  user_id SERIAL PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  email VARCHAR(100) UNIQUE NOT NULL,
  password VARCHAR(100) NOT NULL
);

CREATE TABLE CalculatorHistory (
  history_id SERIAL PRIMARY KEY,
  user_id INT REFERENCES CalculatorUser(user_id),
  calculation_date DATE NOT NULL,
  calculation_details TEXT NOT NULL
);

CREATE TABLE CalculatorSettings (
  user_id INT REFERENCES CalculatorUser(user_id),
  theme VARCHAR(20) DEFAULT 'light',
  currency VARCHAR(3) DEFAULT 'USD',
  language VARCHAR(10) DEFAULT 'en'
);

CREATE TABLE FinancialData (
  data_id SERIAL PRIMARY KEY,
  symbol VARCHAR(10) NOT NULL,
  price DECIMAL(10, 2) NOT NULL,
  last_updated TIMESTAMP
);

CREATE TABLE UserFavorites (
  favorite_id SERIAL PRIMARY KEY,
  user_id INT REFERENCES CalculatorUser(user_id),
  symbol VARCHAR(10) NOT NULL
);
