CREATE TABLE room_status (
    status_id INT PRIMARY KEY,
    room_id INT,
    is_occupied BOOLEAN,
    CHECK (is_occupied IN (TRUE, FALSE))
);

CREATE TABLE check_in_log (
    log_id INT PRIMARY KEY,
    room_id INT,
    check_in_date DATE,
    check_in_time TIME
);

CREATE TABLE check_out_log (
    log_id INT PRIMARY KEY,
    room_id INT,
    check_out_date DATE,
    check_out_time TIME
);

CREATE TABLE room_availability (
    room_id INT PRIMARY KEY,
    check_in_date DATE,
    check_out_date DATE,
    occupancy_status ENUM('available', 'occupied')
);

CREATE TABLE check_out (
    check_out_id INT PRIMARY KEY,
    room_id INT,
    check_out_date DATE,
    guest_id INT
);

CREATE TABLE guest (
    guest_id INT PRIMARY KEY,
    guest_name VARCHAR(50),
    guest_email VARCHAR(50)
);

CREATE TABLE room (
    room_id INT PRIMARY KEY,
    room_number VARCHAR(10),
    room_type VARCHAR(20),
    capacity INT
);
