tables.table_schema:
CREATE TABLE visitor_footfall (
  visitor_id UUID PRIMARY KEY,
  site_id UUID,
  visit_timestamp TIMESTAMP
);

CREATE TABLE tourist_sites (
  site_id UUID PRIMARY KEY,
  site_name VARCHAR(255),
  city VARCHAR(255),
  country VARCHAR(255)
);

CREATE TABLE visitor_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(255) NOT NULL,
    visitors_count INT
);

CREATE TABLE visitor_records (
    record_id INT PRIMARY KEY,
    site_id INT,
    visitor_id INT,
    visit_time TIMESTAMP
);

CREATE TABLE visitor_analytics (
    analytics_id INT PRIMARY KEY,
    site_id INT,
    total_visitors INT,
    peak_hours VARCHAR(50)
);

CREATE TABLE visitor_sites (
  site_id INT PRIMARY KEY,
  site_name TEXT NOT NULL,
  location TEXT NOT NULL
);

CREATE TABLE visitor_logs (
  log_id INT PRIMARY KEY,
  site_id INT REFERENCES visitor_sites(site_id),
  visitor_id INT NOT NULL,
  entry_time TIMESTAMP NOT NULL
);

CREATE TABLE visitor_demographics (
  visitor_id INT PRIMARY KEY,
  age INT,
  gender TEXT
);

CREATE TABLE site_statistics (
  site_id INT PRIMARY KEY,
  total_visitors INT,
  average_age FLOAT
);

CREATE TABLE VisitorRecords (
  visitor_id INT PRIMARY KEY,
  site_id INT,
  visit_date TIMESTAMP,
  visit_duration INTERVAL
);

CREATE TABLE TouristSites (
  site_id INT PRIMARY KEY,
  site_name VARCHAR(255),
  city VARCHAR(100),
  country VARCHAR(100)
);

CREATE TABLE SiteFootfall (
  record_id INT PRIMARY KEY,
  site_id INT,
  footfall_count INT,
  footfall_date DATE
);

CREATE TABLE SiteAnalytics (
  site_id INT PRIMARY KEY,
  avg_daily_footfall FLOAT,
  peak_footfall_time TIME,
  visitor_gender_ratio FLOAT
);

CREATE TABLE VisitorProfiles (
  visitor_id INT PRIMARY KEY,
  visitor_name VARCHAR(255),
  visitor_age INT,
  visitor_gender CHAR(1)
);
