tables.table_schema:
CREATE TABLE Itinerary (
 itinerary_id INT PRIMARY KEY,
 start_date DATE NOT NULL,
 end_date DATE NOT NULL
 );

CREATE TABLE Sightseeing (
 sightseeing_id INT PRIMARY KEY,
 itinerary_id INT REFERENCES Itinerary(itinerary_id),
 location_name TEXT NOT NULL,
 time_of_visit TIME NOT NULL
 );

CREATE TABLE ItineraryDetails (
    id INT PRIMARY KEY,
    itinerary_name TEXT,
    start_date DATE,
    end_date DATE
);

CREATE TABLE SightseeingLocations (
    id INT PRIMARY KEY,
    location_name TEXT,
    location_description TEXT
);

CREATE TABLE Itinerary (
    itinerary_id UUID PRIMARY KEY,
    itinerary_name VARCHAR(255) NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE SightseeingLocations (
    location_id SERIAL PRIMARY KEY,
    location_name VARCHAR(255) NOT NULL,
    city VARCHAR(100),
    country VARCHAR(100)
);

CREATE TABLE Timings (
    timing_id SERIAL PRIMARY KEY,
    location_id SERIAL REFERENCES SightseeingLocations(location_id),
    start_time TIME,
    end_time TIME
);

CREATE TABLE ItinerarySightseeing (
    itinerary_id UUID REFERENCES Itinerary(itinerary_id),
    location_id SERIAL REFERENCES SightseeingLocations(location_id),
    timing_id SERIAL REFERENCES Timings(timing_id)
);

CREATE TABLE Users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(100) NOT NULL,
    email VARCHAR(255) UNIQUE
);
