tables.table_schema:
CREATE TABLE hotel_occupancy (
    hotel_id INT PRIMARY KEY,
    occupancy_rate DECIMAL,
    occupancy_date DATE
);

CREATE TABLE pricing_trends (
    hotel_id INT REFERENCES hotel_occupancy(hotel_id),
    price DECIMAL,
    price_date DATE
);

CREATE TABLE hotel (
    hotel_id INT PRIMARY KEY,
    hotel_name VARCHAR(255),
    location VARCHAR(255),
    occupancy_rate FLOAT
);

CREATE TABLE pricing_trend (
    trend_id INT PRIMARY KEY,
    room_id INT,
    price DECIMAL(10, 2),
    date DATE
);

CREATE TABLE hotel ( 
    hotel_id INT PRIMARY KEY, 
    hotel_name VARCHAR(50) NOT NULL, 
    location VARCHAR(50) NOT NULL, 
    rooms_total INT NOT NULL 
);

CREATE TABLE room_type ( 
    room_type_id INT PRIMARY KEY, 
    room_description VARCHAR(100), 
    room_price DECIMAL(8,2) NOT NULL 
);
