tables.table_schema:
CREATE TABLE frequent_travelers (
    traveler_id INT PRIMARY KEY,
    traveler_name STRING,
    loyalty_points INT
);

CREATE TABLE discount_offers (
    offer_id INT PRIMARY KEY,
    offer_details STRING,
    discount_amount DECIMAL
);

CREATE TABLE frequent_travelers (
    traveler_id INT PRIMARY KEY,
    traveler_name VARCHAR(255),
    email VARCHAR(255),
    loyalty_points INT
);

CREATE TABLE discount_offers (
    offer_id INT PRIMARY KEY,
    offer_name VARCHAR(255),
    offer_type VARCHAR(50),
    discount_amount FLOAT
);

CREATE TABLE redeemed_offers (
    redemption_id INT PRIMARY KEY,
    traveler_id INT,
    offer_id INT,
    redemption_date DATE
);

CREATE TABLE frequent_travelers ( 
    traveler_id UUID PRIMARY KEY, 
    traveler_name VARCHAR(255), 
    loyalty_points INT, 
    membership_level VARCHAR(50) 
);

CREATE TABLE discounted_flights ( 
    flight_id UUID PRIMARY KEY, 
    flight_destination VARCHAR(255), 
    departure_date DATE, 
    discount_amount DECIMAL(10, 2) 
);

CREATE TABLE loyalty_discounts ( 
    discount_id UUID PRIMARY KEY, 
    discount_code VARCHAR(20), 
    discount_percentage DECIMAL(5, 2), 
    expiration_date DATE 
);

CREATE TABLE loyalty_memberships ( 
    membership_id UUID PRIMARY KEY, 
    traveler_id UUID REFERENCES frequent_travelers(traveler_id), 
    enrollment_date DATE, 
    renewal_date DATE 
);

CREATE TABLE purchase_history ( 
    purchase_id UUID PRIMARY KEY, 
    traveler_id UUID, 
    purchase_date DATE, 
    total_amount DECIMAL(10, 2) 
);
