tables.table_schema:
CREATE TABLE historical_weather_data (
    city_id INT,
    date DATE,
    temperature FLOAT,
    weather_condition VARCHAR(255)
);

CREATE TABLE trip_planning (
    trip_id INT,
    city_id INT,
    date DATE,
    trip_name VARCHAR(255)
);

CREATE TABLE weather_data (
    id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    description TEXT
);

CREATE TABLE locations (
    id INT PRIMARY KEY,
    location_name VARCHAR(255),
    latitude FLOAT,
    longitude FLOAT
);

CREATE TABLE trip_plans (
    id INT PRIMARY KEY,
    location_id INT,
    date DATE,
    planned_activities TEXT
);

CREATE TABLE WeatherData (
    id INT PRIMARY KEY,
    date DATE,
    temperature FLOAT,
    weather_condition VARCHAR(255)
);

CREATE TABLE City (
    id INT PRIMARY KEY,
    city_name VARCHAR(255) UNIQUE,
    country VARCHAR(255),
    region VARCHAR(255)
);

CREATE TABLE Tourist (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    age INT,
    nationality VARCHAR(255)
);
