tables.table_schema:
CREATE TABLE travel_expense_reports (
  report_id INT PRIMARY KEY,
  traveler_id INT,
  report_date DATE,
  total_amount DECIMAL
);

CREATE TABLE travelers (
    traveler_id UUID PRIMARY KEY,
    traveler_name TEXT,
    traveler_email TEXT,
    department TEXT
);

CREATE TABLE expense_reports (
    report_id UUID PRIMARY KEY,
    traveler_id UUID REFERENCES travelers (traveler_id),
    report_date DATE,
    total_amount DECIMAL
);
