tables.table_schema:
CREATE TABLE attendees (
    attendee_id INT PRIMARY KEY,
    attendee_name VARCHAR(50),
    attendee_email VARCHAR(100),
    attendee_preferences JSONB
);

CREATE TABLE EventPreferences (
    preference_id UUID PRIMARY KEY,
    attendee_id UUID REFERENCES EventAttendees(attendee_id),
    activity_id UUID REFERENCES EventActivities(activity_id),
    preference_score INT
);

CREATE TABLE attendees (
    attendee_id UUID PRIMARY KEY,
    name VARCHAR,
    email VARCHAR
);

CREATE TABLE events (
    event_id UUID PRIMARY KEY,
    event_name VARCHAR,
    event_date DATE,
    location VARCHAR
);

CREATE TABLE preferences (
    preference_id UUID PRIMARY KEY,
    attendee_id UUID,
    food_preference VARCHAR,
    drink_preference VARCHAR
);

CREATE TABLE activities (
    activity_id UUID PRIMARY KEY,
    event_id UUID,
    activity_name VARCHAR,
    activity_type VARCHAR
);
