tables.table_schema:
CREATE TABLE venues (
  venue_id INT PRIMARY KEY,
  venue_name VARCHAR(255),
  venue_location VARCHAR(255),
  capacity INT
);

CREATE TABLE event_schedule (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255),
    event_date DATE,
    event_time TIME,
    venue_id INT,
);

CREATE TABLE ticketing_information (
    ticket_id INT PRIMARY KEY,
    event_id INT,
    ticket_price DECIMAL(10, 2),
    ticket_quantity INT,
);

CREATE TABLE venue_details (
    venue_id INT PRIMARY KEY,
    venue_name VARCHAR(255),
    venue_location VARCHAR(255),
    venue_capacity INT,
);

CREATE TABLE EventSchedule (
    event_id UUID PRIMARY KEY,
    event_name TEXT NOT NULL,
    event_date DATE NOT NULL,
    event_time TIME NOT NULL
);

CREATE TABLE TicketingInformation (
    ticket_id UUID PRIMARY KEY,
    event_id UUID REFERENCES EventSchedule(event_id),
    ticket_type TEXT NOT NULL,
    ticket_price DECIMAL NOT NULL
);

CREATE TABLE VenueDetails (
    venue_id UUID PRIMARY KEY,
    venue_name TEXT NOT NULL,
    venue_address TEXT NOT NULL,
    venue_capacity INT NOT NULL
);

CREATE TABLE EventManagement (
    event_id UUID REFERENCES EventSchedule(event_id),
    venue_id UUID REFERENCES VenueDetails(venue_id),
    ticket_id UUID REFERENCES TicketingInformation(ticket_id),
    PRIMARY KEY (event_id, venue_id, ticket_id)
);

CREATE TABLE tickets (
	ticket_id UUID PRIMARY KEY,
	event_id UUID NOT NULL,
	ticket_price DECIMAL(10,2) NOT NULL,
	ticket_quantity INT NOT NULL
);

CREATE TABLE venues (
	venue_id UUID PRIMARY KEY,
	venue_name VARCHAR(255) NOT NULL,
	venue_address VARCHAR(255) NOT NULL
);

CREATE TABLE event_venues (
	event_id UUID,
	venue_id UUID,
	PRIMARY KEY (event_id, venue_id)
);

CREATE TABLE bookings (
	booking_id UUID PRIMARY KEY,
	event_id UUID NOT NULL,
	customer_name VARCHAR(255) NOT NULL,
	booking_date DATE NOT NULL
);
