tables.table_schema:
CREATE TABLE rooms (
    room_id SERIAL PRIMARY KEY,
    room_number INT NOT NULL,
    room_type VARCHAR(50),
    max_occupancy INT
);

CREATE TABLE rooms (
    room_number INT PRIMARY KEY,
    room_type VARCHAR(50),
    rate DECIMAL(10, 2),
    capacity INT
);

CREATE TABLE payments (
    payment_id INT PRIMARY KEY,
    booking_id INT,
    total_amount DECIMAL(10, 2),
    payment_date DATE
);
