tables.table_schema:
CREATE TABLE feedback_table (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    rating INT,
    comments VARCHAR
);

CREATE TABLE review_table (
    review_id INT PRIMARY KEY,
    customer_id INT,
    review_text TEXT,
    review_date DATE
);

CREATE TABLE feedbacks (
    feedback_id INT PRIMARY KEY,
    customer_id INT,
    rating FLOAT,
    feedback_text TEXT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    email_address TEXT,
    phone_number TEXT
);

CREATE TABLE reviews (
    review_id INT PRIMARY KEY,
    feedback_id INT,
    review_text TEXT,
    review_date TIMESTAMP
);

CREATE TABLE GuestFeedback (
  feedback_id INT PRIMARY KEY,
  guest_id INT,
  feedback_text TEXT,
  feedback_date DATE
);

CREATE TABLE GuestDetails (
  guest_id INT PRIMARY KEY,
  guest_name VARCHAR(50),
  email_address VARCHAR(100),
  phone_number VARCHAR(15)
);

CREATE TABLE FeedbackAnalysis (
  analysis_id INT PRIMARY KEY,
  feedback_id INT,
  sentiment_score INT,
  analysis_date DATE
);

CREATE TABLE ServiceImprovement (
  improvement_id INT PRIMARY KEY,
  analysis_id INT,
  improvement_text TEXT,
  action_date DATE
);

CREATE TABLE customer_feedback (
  customer_id INT,
  feedback_text TEXT,
  rating INT
);

CREATE TABLE product_reviews (
  review_id INT,
  product_id INT,
  review_text TEXT,
  rating INT
);

CREATE TABLE service_ratings (
  rating_id INT,
  service_id INT,
  rating_score INT
);

CREATE TABLE feedback_analysis (
  analysis_id INT,
  feedback_id INT,
  analysis_result TEXT
);

CREATE TABLE guest_satisfaction (
  satisfaction_id INT,
  guest_id INT,
  satisfaction_score INT
);
