CREATE TABLE attractions (
    id UUID PRIMARY KEY,
    name VARCHAR(255),
    location VARCHAR(255),
    description TEXT
);

CREATE TABLE attractions (
   attraction_id UUID PRIMARY KEY,
   name STRING,
   location STRING,
   description TEXT
);

CREATE TABLE activities (
   activity_id UUID PRIMARY KEY,
   name STRING,
   duration INT,
   description TEXT
);

CREATE TABLE tours (
   tour_id UUID PRIMARY KEY,
   name STRING,
   cost DECIMAL,
   duration INT
);
