tables.table_schema:
CREATE TABLE rewards (
    player_id INT NOT NULL,
    reward_points INT NOT NULL,
    last_update_date DATE
);

CREATE TABLE PlayerRewards (
    player_id INT PRIMARY KEY,
    reward_points INT,
    reward_level VARCHAR(255)
);

CREATE TABLE player_rewards (
    player_id INT PRIMARY KEY,
    reward_points INT,
    reward_level VARCHAR(50)
);

CREATE TABLE player_behavior (
    player_id INT REFERENCES player_rewards(player_id),
    total_bets_placed INT,
    total_wins INT,
    favorite_game VARCHAR(100)
);

CREATE TABLE rewards (
    reward_id INT PRIMARY KEY,
    player_id INT,
    reward_type VARCHAR(50),
    reward_amount DECIMAL
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    player_email VARCHAR(50),
    player_status VARCHAR(50)
);
