    ticket_id SERIAL PRIMARY KEY,
    visitor_id INT,
    ticket_price DECIMAL,
    purchase_date DATE
);

CREATE TABLE visitor_demographics (
    visitor_id INT PRIMARY KEY,
    age INT,
    gender ENUM('Male', 'Female', 'Other'),
    location VARCHAR(50)
);

CREATE TABLE ticket_sales (
  ticket_id INT NOT NULL,
  visitor_id INT NOT NULL,
  ticket_price DECIMAL NOT NULL,
  purchase_date DATE NOT NULL
);

CREATE TABLE ride_wait_times (
  ride_id INT NOT NULL,
  ride_name VARCHAR(50) NOT NULL,
  wait_time_minutes INT NOT NULL,
  park_area VARCHAR(50)
);

CREATE TABLE ride_wait_times (
    ride_id INT PRIMARY KEY,
    ride_name VARCHAR(50),
    wait_time_minutes INT,
    last_updated TIMESTAMP
);

CREATE TABLE park_information (
    park_id INT PRIMARY KEY,
    park_name VARCHAR(50),
    location VARCHAR(100),
    establishment_date DATE
);

CREATE TABLE ride_wait_times (
    ride_id UUID PRIMARY KEY,
    ride_name VARCHAR,
    wait_time INT,
    last_updated TIMESTAMP
);

CREATE TABLE visitor_tickets (
    ticket_id UUID PRIMARY KEY,
    visitor_id UUID,
    purchase_date TIMESTAMP
);

CREATE TABLE ride_details (
    ride_id UUID PRIMARY KEY,
    ride_name VARCHAR,
    description TEXT,
    capacity INT
);
