tables.table_schema:
CREATE TABLE FoodDiaryEntry (
  entry_id INT PRIMARY KEY,
  entry_date DATE,
  food_name VARCHAR(255),
  calorie_count INT
);

CREATE TABLE food_diary (
  entry_id UUID PRIMARY KEY,
  date DATE,
  food_name VARCHAR(255),
  calorie_count INT
);

CREATE TABLE recipe_suggestions (
  recipe_id UUID PRIMARY KEY,
  recipe_name VARCHAR(255),
  ingredients TEXT,
  dietary_preferences VARCHAR(255)
);

CREATE TABLE food_entry (
  entry_id INT PRIMARY KEY,
  food_name VARCHAR(255),
  calorie_amount INT,
  entry_date DATE
);

CREATE TABLE CalorieIntake (
  intake_id INT PRIMARY KEY,
  date DATE,
  total_calories INT
);

CREATE TABLE Recipes (
  recipe_id INT PRIMARY KEY,
  recipe_name VARCHAR,
  ingredients VARCHAR,
  preparation_steps VARCHAR
);

CREATE TABLE DietaryPreferences (
  pref_id INT PRIMARY KEY,
  user_id INT,
  preference VARCHAR
);

CREATE TABLE User (
  user_id INT PRIMARY KEY,
  username VARCHAR,
  email VARCHAR,
  age INT
);
