tables.table_schema:
CREATE TABLE customer_preferences (
    customer_id INT PRIMARY KEY,
    room_type VARCHAR(50),
    pillow_preference VARCHAR(20),
    extra_amenities TEXT
);

CREATE TABLE amenities_catalog (
    amenity_id INT PRIMARY KEY,
    amenity_name VARCHAR(50),
    amenity_description TEXT,
    price DECIMAL(10,2)
);

CREATE TABLE customer_preferences (
  customer_id UUID PRIMARY KEY,
  room_type VARCHAR,
  bed_preference VARCHAR,
  tv_preference BOOLEAN
);

CREATE TABLE room_types (
  room_type_id UUID PRIMARY KEY,
  room_description VARCHAR,
  room_price DECIMAL
);

CREATE TABLE bed_options (
  bed_option_id UUID PRIMARY KEY,
  bed_type VARCHAR,
  bed_size VARCHAR
);

CREATE TABLE customers (
	customer_id INT PRIMARY KEY,
	name VARCHAR(50) NOT NULL,
	email VARCHAR(50) UNIQUE,
	phone_number VARCHAR(15)
);

CREATE TABLE rooms (
	room_id INT PRIMARY KEY,
	room_type VARCHAR(50) NOT NULL,
	price DECIMAL(10, 2),
	capacity INT
);

CREATE TABLE amenities (
	amenity_id INT PRIMARY KEY,
	amenity_name VARCHAR(50) NOT NULL,
	category VARCHAR(50),
	price DECIMAL(10, 2)
);
