tables.table_schema:
CREATE TABLE guests (
    guest_id INT PRIMARY KEY,
    guest_name TEXT,
    guest_age INT,
    guest_gender TEXT
);

CREATE TABLE marketing_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name TEXT,
    campaign_type TEXT
);

CREATE TABLE GuestDemographics (
    guest_id INT PRIMARY KEY,
    guest_name VARCHAR(100),
    guest_email VARCHAR(100),
    guest_age INT
);

CREATE TABLE ReservationHistory (
    reservation_id INT PRIMARY KEY,
    guest_id INT REFERENCES GuestDemographics(guest_id),
    checkin_date DATE,
    checkout_date DATE
);

CREATE TABLE MarketingCampaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(100),
    target_age_group VARCHAR(50),
    target_interests VARCHAR(200)
);

CREATE TABLE guest_preferences (
    guest_id INT REFERENCES guests(guest_id),
    preference_type TEXT,
    preference_value TEXT,
    PRIMARY KEY (guest_id, preference_type)
);
