CREATE TABLE bills (
  bill_id INT PRIMARY KEY,
  bill_name VARCHAR(255),
  introduced_date DATE,
  status VARCHAR(50)
);

CREATE TABLE laws (
  law_id INT PRIMARY KEY,
  law_name VARCHAR(255),
  enactment_date DATE,
  category VARCHAR(50)
);

CREATE TABLE legislators (
  legislator_id INT PRIMARY KEY,
  full_name VARCHAR(255),
  party_affiliation VARCHAR(50),
  district VARCHAR(50)
);

