tables.table_schema:
CREATE TABLE demographic_data (
    region_id UUID PRIMARY KEY,
    region_name VARCHAR NOT NULL,
    population_size INT,
    political_opinion VARCHAR
);

CREATE TABLE opinion_distribution (
    id INT PRIMARY KEY,
    region VARCHAR(255),
    political_opinion VARCHAR(50),
    opinion_count INT
);

CREATE TABLE demographics (
    region_id SERIAL PRIMARY KEY,
    region_name VARCHAR(100),
    population INT,
    median_age FLOAT
);

CREATE TABLE political_opinions (
    opinion_id SERIAL PRIMARY KEY,
    region_id INT REFERENCES demographics(region_id),
    political_opinion VARCHAR(100),
    approval_rating FLOAT
);

CREATE TABLE region_stats (
    region_id INT REFERENCES demographics(region_id),
    num_polls INT,
    avg_approval_rating FLOAT,
    PRIMARY KEY (region_id)
);

CREATE TABLE opinions (
    opinion_id INT PRIMARY KEY,
    region_id INT,
    political_affiliation VARCHAR(30),
    opinion_text TEXT
);

CREATE TABLE politicians (
    politician_id INT PRIMARY KEY,
    name VARCHAR(50),
    region_id INT,
    political_party VARCHAR(50)
);

CREATE TABLE regions (
  region_id INT PRIMARY KEY,
  region_name VARCHAR(255),
  population INT
);

CREATE TABLE political_parties (
  party_id INT PRIMARY KEY,
  party_name VARCHAR(50),
  ideology VARCHAR(100)
);

CREATE TABLE poll_results (
  result_id INT PRIMARY KEY,
  poll_id INT,
  citizen_id INT,
  vote VARCHAR(10)
);
