tables.table_schema:
CREATE TABLE supporters (
    supporter_id INT PRIMARY KEY,
    supporter_name VARCHAR,
    supporter_email VARCHAR
);

CREATE TABLE volunteers (
    volunteer_id INT PRIMARY KEY,
    volunteer_name VARCHAR,
    volunteer_email VARCHAR
);

CREATE TABLE campaign_communication (
    communication_id INT PRIMARY KEY,
    communication_type VARCHAR,
    communication_content TEXT,
    communication_date DATE
);

CREATE TABLE campaign_contacts (
    contact_id INT PRIMARY KEY,
    contact_type VARCHAR(20),
    contact_name VARCHAR(50),
    contact_info VARCHAR(100)
);

CREATE TABLE supporters (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(15)
);

CREATE TABLE campaigns (
    id INT PRIMARY KEY,
    title VARCHAR(255),
    description TEXT
);

CREATE TABLE supporters_campaigns (
    supporter_id INT,
    campaign_id INT,
    FOREIGN KEY (supporter_id) REFERENCES supporters(id),
    FOREIGN KEY (campaign_id) REFERENCES campaigns(id)
);
