tables.table_schema:
CREATE TABLE LobbyingActivities (
    id INT PRIMARY KEY,
    activity_name VARCHAR(255),
    lobbyist_name VARCHAR(255),
    date DATE
);

CREATE TABLE Interactions (
    id INT PRIMARY KEY,
    interaction_type VARCHAR(255),
    contact_name VARCHAR(255),
    interaction_date DATE
);

CREATE TABLE lobbying_organizations (
    id INT PRIMARY KEY,
    organization_name TEXT,
    sector TEXT
);

CREATE TABLE influence_monitoring (
    id INT PRIMARY KEY,
    target_person VARCHAR(255),
    relationship_description TEXT,
    red_flag BOOLEAN
);

CREATE TABLE conflicts_of_interest (
    id INT PRIMARY KEY,
    party1 VARCHAR(255),
    party2 VARCHAR(255),
    conflict_description TEXT
);

CREATE TABLE lobby_interests (
    lobbyist_id INT PRIMARY KEY,
    interest_name TEXT,
    influence_level INT
);

CREATE TABLE lobbyists (
    lobbyist_id INT PRIMARY KEY,
    name TEXT,
    organization TEXT,
    contact_info TEXT
);

CREATE TABLE conflicts_of_interest (
    conflict_id INT PRIMARY KEY,
    conflict_description TEXT,
    lobbyist_id INT,
    organization TEXT
);
