tables.table_schema:
CREATE TABLE voter_sentiment (
    sentiment_id UUID PRIMARY KEY,
    voter_id UUID,
    issue_id UUID,
    sentiment_score INT
);

CREATE TABLE voter_info (
    voter_id INT PRIMARY KEY,
    voter_name VARCHAR,
    age INT,
    gender VARCHAR
);

CREATE TABLE political_issues (
    issue_id INT PRIMARY KEY,
    issue_topic VARCHAR,
    opinion_rating INT
);
