tables.table_schema:
CREATE TABLE Leadership_Development_Plans (
	plan_id INT PRIMARY KEY,
	plan_name VARCHAR(255),
	start_date DATE,
	end_date DATE
);

CREATE TABLE Goals (
  goal_id INT PRIMARY KEY,
  goal_description TEXT,
  goal_status VARCHAR(50),
  plan_id INT REFERENCES LeadershipDevelopmentPlans(plan_id)
);

CREATE TABLE Objectives (
  objective_id INT PRIMARY KEY,
  objective_description TEXT,
  objective_status VARCHAR(50),
  goal_id INT REFERENCES Goals(goal_id)
);

CREATE TABLE leadership_development_plans (
    id INT PRIMARY KEY,
    plan_name VARCHAR(255),
    start_date DATE,
    end_date DATE,
);

CREATE TABLE progress_tracking (
    id INT PRIMARY KEY,
    plan_id INT,
    goal_name VARCHAR(255),
    progress_percentage INT,
);

CREATE TABLE objectives (
    id INT PRIMARY KEY,
    goal_id INT,
    objective_description TEXT,
);

CREATE TABLE users (
    id INT PRIMARY KEY,
    user_name VARCHAR(255),
    email VARCHAR(255),
);

CREATE TABLE Goal_Objectives ( 
    goal_id INT PRIMARY KEY, 
    plan_id INT, 
    goal_description TEXT, 
    completion_status BOOLEAN 
);

CREATE TABLE Employee_Details ( 
    employee_id INT PRIMARY KEY, 
    employee_name VARCHAR(255), 
    department_id INT, 
    position VARCHAR(255) 
);

CREATE TABLE Department ( 
    department_id INT PRIMARY KEY, 
    department_name VARCHAR(255), 
    manager_id INT 
);
