tables.table_schema:
CREATE TABLE regulations (
    id INT PRIMARY KEY,
    regulation_name TEXT,
    regulation_description TEXT
);

CREATE TABLE regulations (
    id INT PRIMARY KEY,
    policy_name VARCHAR(255),
    category VARCHAR(100),
    description TEXT
);

CREATE TABLE departments (
    id INT PRIMARY KEY,
    department_name VARCHAR(255),
    responsible_person VARCHAR(100)
);

CREATE TABLE regulations (
    regulation_id SERIAL PRIMARY KEY,
    regulation_name VARCHAR(255),
    regulation_description TEXT
);

CREATE TABLE policies (
    policy_id SERIAL PRIMARY KEY,
    policy_name VARCHAR(255),
    policy_description TEXT,
    regulation_id INT REFERENCES regulations(regulation_id)
);

CREATE TABLE departments (
    department_id SERIAL PRIMARY KEY,
    department_name VARCHAR(255)
);

CREATE TABLE department_policies (
    department_id INT REFERENCES departments(department_id),
    policy_id INT REFERENCES policies(policy_id)
);

CREATE TABLE regulations (
    id SERIAL PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE policies (
    id SERIAL PRIMARY KEY,
    policy_number INT UNIQUE NOT NULL,
    effective_date DATE,
    description TEXT
);

CREATE TABLE categories (
    id SERIAL PRIMARY KEY,
    category_name VARCHAR(100) UNIQUE NOT NULL
);

CREATE TABLE agencies (
    id SERIAL PRIMARY KEY,
    agency_name VARCHAR(100) UNIQUE NOT NULL,
    address TEXT,
    contact_person VARCHAR(50)
);
